%% Test
run run_parmeters.m
x = (1:param.sys.n)';
[hs, dhsdx] = constraint_symmetry(x, param);

if (all(isapprox(dhsdx,dhsdx_maple{param.sys.dim_S - lower + 1}),"all") && ...
        all(isapprox(hs, hs_maple{param.sys.dim_S - lower + 1}'),"all"))
    fprintf("Maple and matlab implementations match for dim_S=%d!\n", param.sys.dim_S);
else
    fprintf("Maple and Matlab mismatch\n");
end